var ran = (0..QUOTES.size).shuffled().take(20).toList()
class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            INSPIRETheme {
                Main()
            }
        }
    }
}

@Composable
fun Main(){
    val scaffoldState = rememberScaffoldState(rememberDrawerState(DrawerValue.Closed))
    val coroutineScope = rememberCoroutineScope()
    Scaffold(
        scaffoldState = scaffoldState,
        topBar = {
            TopAppBar(title = {Text("INSPIRE", color = Color.White)},backgroundColor = Color(0xFF0F9D58),
                navigationIcon = {Icon(
                    imageVector = Icons.Default.Menu,
                    contentDescription = "Menu",
                    modifier = Modifier.clickable(onClick = {
                        coroutineScope.launch {
                            scaffoldState.drawerState.open()
                        }}),
                    tint = Color.White)
                })
        },
        // pass the bottomBar we created
        bottomBar = {
            BottomAppBar(
                backgroundColor = Color(0xFF0F9D58)
            ) {
                Text(text = "Made With ❣️By The Coders Group", color = Color.Black, style = TextStyle(fontWeight = FontWeight.ExtraBold, fontFamily = FontFamily.SansSerif), modifier = Modifier.fillMaxWidth(), textAlign = TextAlign.Center)
            }
        },
        content = {
            ShowAllNews()
        },

        // pass the drawer
        drawerContent = {
            Column(
                Modifier
                    .background(Color.White)
                    .fillMaxSize()
            ) {
                Text(
                    text = "Authors", modifier = Modifier
                        .padding(top = 10.dp)
                        .fillMaxWidth()
                        .clip(RoundedCornerShape(10.dp)), color = Color.Red,
                    textAlign = TextAlign.Center,
                    fontSize = 35.sp,
                    style = TextStyle(fontWeight = FontWeight.Bold)
                )
                ran.forEach{
                    val author = AUTHOR[it]
                    if (author != ""){
                        Text(text = "$author", modifier = Modifier.padding(8.dp), color = Color.Black)
                    }
                }
            }
        }
    )
}

@Composable
fun ShowAllNews() {
    Box(
        Modifier
            .fillMaxSize()
            .background(color = Color.LightGray)
            .padding(top = 0.dp)
            .scrollable(rememberScrollState(), orientation = Orientation.Vertical),
        contentAlignment = Alignment.TopCenter,

        ) {
        Column(modifier = Modifier
            .fillMaxSize()
            .background(color = Color.Transparent)
            .verticalScroll(rememberScrollState()),
            verticalArrangement = Arrangement.spacedBy(20.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            var i = 0;
            ran.forEach {
                NewsUI(title = QUOTES[it], author = AUTHOR[it],i=i++)
            }
        }
    }
}
@Composable
fun NewsUI(title: String,author: String,i: Int) {
    Box(
        modifier = Modifier
            .background(color = Color.Transparent)
            .fillMaxWidth(0.7f)
            .clip(RoundedCornerShape(10.dp))
            .padding(all = 0.dp),
        contentAlignment = Alignment.Center
    ){
        Image(painter = painterResource(id = IMAGES[i%48]), contentDescription = null, modifier = Modifier.fillMaxSize())
        Column {
            Text(
                text = "$title", modifier = Modifier
                    .padding(horizontal = 10.dp)
                    .fillMaxSize()
                    .clip(RoundedCornerShape(10.dp)), color = Color.White,
                textAlign = TextAlign.Center,
                style = TextStyle(fontWeight = FontWeight.Bold, fontStyle = FontStyle.Italic),
                fontSize = 25.sp
            )
            Text(
                text = "~$author", modifier = Modifier
                    .padding(all = 10.dp)
                    .fillMaxSize()
                    .clip(RoundedCornerShape(10.dp))
                ,color = Color.Red,
                style = TextStyle(fontStyle = FontStyle.Italic),
                textAlign = TextAlign.End,
                fontSize = 15.sp
            )
        }

    }

}
